                    var clickedRow = null;
                    function dbClickRow(e) {
                        if(clickedRow == e.sender._data[0].uid){
                            clickedRow = null;
                            dbClickUpdate();
                        } else {
                            clickedRow = e.sender._data[0].uid;
                        }
                    }

                 

