var MessageTypes = { Success: 0, Error: 1, Information: 2, Warning: 3, Empty: 4 };

var Message = function (code, message, title) {
    switch (code) {
        case 0:
            this.type = 'S'; break;

        case 1:
            this.type = 'E'; break;

        case 2:
            this.type = 'I'; break;

        case 3:
            this.type = 'W'; break;

        case 4:
            return [];

        default:
            this.type = ''; break;
    }
    this.message = message || ''; this.title = title || '';
};


