    $scope.ValidateIdNumber = function (NicType) {
        if (NicType == 2) {
            var id = $scope.BulkPayment.OldNic;
            var regex = /^[0-9]\d*[V|v|x|X]$/;
            if (id.length != 10) {
                return false;
            }
            if (!regex.test(id)) {
                return false;
            } else {
                return true;
            }

        } else {
            var id = $scope.BulkPayment.NewNic;
            var regex = /^[0-9]\d*$/;
            if (id.length != 12) {
                return false;
            }
            if (!regex.test(id)) {
                return false;
            } else {
                return true;
            }
        }
    }

