            PinUserId: $scope.payCancel.PinRequestUsers,
            OutletId: PinRequestDetails.OutletCode,
            WfId: PinRequestDetails.WorkFlowRef,
            PinType: "RESEND",
            WfReqUserId: PinRequestDetails.UserId

